import './App.css';
import logogato from "../src/imagenes/logo.gif"
import Boton from "./componentes/Boton.jsx";
import Display from './componentes/Display';
import BotonClear from './componentes/BotonClear';
import {useState} from "react";

function App() {

  const[input, setInput]=useState('hola');

  const agregarInput=val=>{
    setInput(input+val)
  };


  return (
    <div className="App">
      <div className="main-logo">
        <img
          src={logogato}
          className="logo"
          alt="logo gato"/>
      </div>
      <div className="container-calculadora">
        <Display input={input}/>
        <div className="fila">
          <Boton>1</Boton>
          <Boton>2</Boton>
          <Boton>3</Boton>
          <Boton>+</Boton>
        </div>
        <div className="fila">
          <Boton>4</Boton>
          <Boton>5</Boton>
          <Boton>6</Boton>
          <Boton>-</Boton>
        </div>
        <div className="fila">
          <Boton>7</Boton>
          <Boton>8</Boton>
          <Boton>9</Boton>
          <Boton>*</Boton>
        </div>
        <div className="fila">
          <Boton>.</Boton>
          <Boton>0</Boton>
          <Boton>=</Boton>
          <Boton>/</Boton>
        </div>
        <div className="fila">
          <BotonClear>Clear</BotonClear>
        </div>
      </div>
    </div>
  );
}

export default App;
